$background-Main: #fff;
$sizing-Border: border-box;
$paddingBox: 10px;
$background-Column: #dadadaba;
$background-Rows: #fff;

* {
    padding: 0;
    margin: 0;
}
body {
    background-color: $background-Main;
    width: 100%;
}
#app {
    nav {
        img {
            width: 60px;
        }

        ul {
            li {
                padding: 0 10px;
            }
            li:hover {
                background-color: #48282870;
                cursor: pointer;
            }
        }
    }
}
.container {
    padding: $paddingBox;
    box-sizing: $sizing-Border;
    display: flex;
    justify-content: space-evenly;

    .column {
        padding: $paddingBox;
        box-sizing: $sizing-Border;
        width: 24.5%;
        background-color: $background-Column;

        .title {
            font-size: 25px;
            font-family: sans-serif;
            margin-bottom: 10px;
            color: #312f39;
        }

        .card {
            box-sizing: $sizing-Border;
            background-color: $background-Rows;
            margin-bottom: 10px;
            padding: $paddingBox;
            border-radius: 5px;

            .upper {
                margin-bottom: 10px;
                font-weight: bold;
            }

            .bottom {
                display: flex;
                justify-content: space-between;
                align-items: center;

                .left-bottom {
                    display: flex;

                    .date, .author {
                        display: flex;
                        align-items: flex-end;

                        .icon-date {
                            color: #0674b7ed;
                        }

                        .icon-author {
                            color: #df9466;
                        }
                    }
                }

                .right-bottom {
                    display: flex;

                    .icon-edit {
                        color: #2fa206;
                    }

                    .icon-edit:hover {
                        color: #93cc7e;
                        cursor: pointer;
                    }

                    .icon-delete {
                        color: #c81717;
                    }

                    .icon-delete:hover {
                        color: #d55a5a;
                        cursor: pointer;
                    }

                    .icon-reorder {
                        color: #092fa2;
                    }

                    .icon-reorder:hover {
                        color: #5e77c1;
                        cursor: pointer;
                    }

                    .photo img{
                        width: 40px;
                    }
                }

            }
        }

        .newForm {
            background-color: #f2c798db;
            border-radius: 5px;

            .input-field {

                input {
                    color: #4f4242;
                }
                label {
                    color: #4f4242;
                }
            }

            .s6 {
                margin-top: 0;
                margin-bottom: 0;
                text-align: center;
            }

            .btnFormTask {
                display: flex;
                align-items: center;
                padding: 5px 10px;
                border-radius: 5px;
            }
            .btnFormTask:hover {
                cursor: pointer;
                box-shadow: 0 2px 2px 0 rgba(0,0,0,0.14),0 3px 1px -2px rgba(0,0,0,0.12),0 1px 5px 0 rgba(0,0,0,0.2);
            }
        }
    }
}

.own {
    background-color: #fff3d1f0 !important;
}
